def solve1(s):
    return str.count("(") * 1 + str.count(")") * -1


def solve2(s):
    for i in xrange(1, len(s) + 1):
        str = s[0:i]
        val = str.count("(") * 1 + str.count(")") * -1
        if val < 0:
            return i


